    public static function getFamousFirstnames()
    {
        $names = array_merge(self::getWorldFirstnames(), self::getFrenchFirstnames());
        foreach ($names as &$name) {
            $name = \Anakeen\Core\Utils\Strings::mbUcfirst(mb_strtolower(trim($name)));
        }
        return $names;
    }

