    public static function getManyLastnames()
    {
        $names = file(__DIR__ . "/lastNameData.txt");
        foreach ($names as &$name) {
            $name = \Anakeen\Core\Utils\Strings::mbUcfirst(mb_strtolower(trim($name)));
        }

        return $names;
    }

    public static function getRandomDate($min = 1950, $max = 2000)
    {
        return sprintf("%04d-%02d-%02d", rand($min, $max), rand(1, 12), rand(1, 28));
    }

    public static function getRandomName()
    {
        self::initNameSet();
        return sprintf(
            "%s %s",
            self::$nameSet["firstName"][rand(0, count(self::$nameSet["firstName"]) - 1)],
            self::$nameSet["lastName"][rand(0, count(self::$nameSet["lastName"]) - 1)]
        );
    }

    public static function getRandomFirstName()
    {
        self::initNameSet();
        return self::$nameSet["firstName"][rand(0, count(self::$nameSet["firstName"]) - 1)];
    }

    public static function getRandomLastName()
    {
        self::initNameSet();
        return self::$nameSet["lastName"][rand(0, count(self::$nameSet["lastName"]) - 1)];
    }


    public static function getRandomTown()
    {
        if (! isset(self::$townSet)) {
            self::$townSet = file(__DIR__ . "/townsData.txt");
        }
        return self::$townSet[rand(0, count(self::$townSet) - 1)];
    }
    public static function getRandomSociety()
    {
        if (! isset(self::$societySet)) {
            self::$societySet = file(__DIR__ . "/societiesData.txt");
        }
        return self::$societySet[rand(0, count(self::$societySet) - 1)];
    }

    protected static function initNameSet()
    {
        if (!self::$nameSet) {
            self::$nameSet["firstName"] = array_merge(self::getFamousFirstnames(), self::getFrenchFirstnames());
            self::$nameSet["lastName"] = array_merge(self::getFamousLastnames(), self::getManyLastnames());
        }
    }
