<?php

namespace Anakeen\AdminCenter\SmartStructures\HubAdminCenterParameters;

use Anakeen\Ui\UIGetAssetPath;
use SmartStructure\Hubconfigurationvue;

class HubAdminCenterParametersVueBehavior extends Hubconfigurationvue
{

    public function registerHooks()
    {
        parent::registerHooks();
    }

    /**
     * Get component specific configuration, to display it correctly with its options
     *
     * @return array
     */
    protected function getComponentConfiguration()
    {
        return [
            // Name of the Vue.js component
            "name" => "ank-admin-parameter",

            // Properties to use for the components
            "props" => []
        ];
    }

    /**
     * @return array
     * @throws \Anakeen\Ui\Exception
     */
    public static function getJSAsset()
    {
        $asset = UIGetAssetPath::getElementAssets("parameterManager", UIGetAssetPath::isInDebug() ? "dev" : "prod");
        if (isset($asset["parameter-manager"]["js"])) {
            return [
                $asset["parameter-manager"]["js"]
            ];
        }
        $assets = parent::getAssets(\SmartStructure\Hubadmincenterparametersvue::familyName);
        return $assets["js"];
    }

    protected function getEntryOptions()
    {
        $entryOptions = parent::getEntryOptions(); // TODO: Change the autogenerated stub
        $entryOptions["libName"] = "AdminParametersManager";
        return $entryOptions;
    }
}
