<?php

namespace Anakeen\AdminCenter\SmartStructures\HubAuthenticationTokens;

use Anakeen\Ui\UIGetAssetPath;
use SmartStructure\Hubconfigurationvue;

class HubAuthenticationTokensVueBehavior extends Hubconfigurationvue
{

    public function registerHooks()
    {
        parent::registerHooks();
    }

    /**
     * Get component specific configuration, to display it correctly with its options
     *
     * @return array
     */
    protected function getComponentConfiguration()
    {
        return [
            // Name of the Vue.js component
            "name" => "ank-hub-authentication-tokens",

            // Properties to use for the components
            "props" => []
        ];
    }

    /**
     * @return array
     * @throws \Anakeen\Ui\Exception
     */
    public static function getJSAsset()
    {
        $asset = UIGetAssetPath::getElementAssets("tokenManager", UIGetAssetPath::isInDebug() ? "dev" : "prod");
        if (isset($asset["token-manager"]["js"])) {
            return [
                $asset["token-manager"]["js"]
            ];
        }

        $assets = parent::getAssets(\SmartStructure\Hubauthenticationtokensvue::familyName);
        return $assets["js"];
    }

    protected function getEntryOptions()
    {
        $entryOptions = parent::getEntryOptions(); // TODO: Change the autogenerated stub
        $entryOptions["libName"] = "AdminTokenManager";
        return $entryOptions;
    }
}
